import React, { useState } from 'react';
import TranscribeYouTube from './TranscribeYouTube';
import Login from './Login';
import '../App.css';

function App() {
  const [user, setUser] = useState<string | null>(null);

  return (
    <div className="app-container">
      <header style={{ textAlign: 'center', marginBottom: 24 }}>
        <img src="/logo.png" alt="EchoDraft Logo" style={{ height: 64, marginBottom: 8 }} />
        <h1>EchoDraft</h1>
      </header>
      {!user ? (
        <Login onLogin={setUser} />
      ) : (
        <>
          <p>Welcome, {user}! <button onClick={() => setUser(null)}>Logout</button></p>
          <TranscribeYouTube />
        </>
      )}
    </div>
  );
}

export default App;
